// About.js
import React from "react";
import "./About.css";

const About = () => {
  return (
    <div className="about-section">
      <h1>About US</h1>
      <img
        src="https://static.vecteezy.com/system/resources/previews/004/589/363/original/cartoon-man-and-woman-drinking-tea-looking-into-each-other-s-eyes-couple-love-winter-camping-vector.jpg"
        alt="About Us"
        className="aboutImg"
      />
      <div className="text">
        <h1>Why choose us?</h1>
        <p>
          Welcome to our House Essentials application! Find everything from{" "}
          <br />
          furniture to home decor, all in one place.
        </p>
        <p>
          Our goal is to provide you with quality items at affordable prices.
          Explore now!
        </p>
      </div>
    </div>
  );
};

export default About;
