// About.js
import React from "react";
import "./About.css";

const About = () => {
  return (
    <div className="about-section">
      <h1>About US</h1>
      <img
        src="https://static.vecteezy.com/system/resources/previews/004/589/363/original/cartoon-man-and-woman-drinking-tea-looking-into-each-other-s-eyes-couple-love-winter-camping-vector.jpg"
        alt="About Us"
        className="aboutImg"
      />
      <div className="text">
        <h1>Why choose us?</h1>
        <div className="about-section">
          <p>
            Welcome to House Essentials! We specialize in providing top-quality{" "}
            <br />
            products for every corner of your home. <br /> From stylish
            furniture and kitchen gadgets to elegant home decor, <br />
            we offer a wide range of items to enhance your living space.
          </p>
          <p>
            Our mission is to simplify your home improvement journey by offering{" "}
            <br />a curated selection of essential and innovative products. We
            believe <br />
            in delivering exceptional value and unmatched convenience to our
            customers.
          </p>
        </div>
      </div>
    </div>
  );
};

export default About;
