import React, { useState, useEffect } from "react";
import "./Header.css";
import Theme from "./Theme"; // Import the Theme function

const Header = () => {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const savedTheme = localStorage.getItem("theme");
    return savedTheme === "dark";
  });

  useEffect(() => {
    Theme(isDarkMode); // Call Theme function to apply the theme
    localStorage.setItem("theme", isDarkMode ? "dark" : "light");
  }, [isDarkMode]);

  const themeToggle = () => {
    setIsDarkMode((prevMode) => !prevMode);
  };

  return (
    <div className="main">
      <img
        src="https://logos.textgiraffe.com/logos/logo-name/Ayesha-designstyle-pastel-m.png"
        alt="Logo"
        className="logo"
      />
      <nav>
        <div className="nav-links">
          <ul>
            <li>
              <a href="/">Home</a>
            </li>
            <li>
              <a href="/about">About</a>
            </li>
            <li>
              <a href="/contact">Contact</a>
            </li>
            <li>
              <a href="/terms">Terms of use</a>
            </li>
            <li>
              <a href="/joinus">Join Us</a>
            </li>
          </ul>
        </div>
        <div className="nav-buttons">
          <div className="all">
            <input
              type="checkbox"
              className="theme-checkbox"
              id="theme-checkbox"
              onChange={themeToggle} // Trigger themeToggle on change
              checked={isDarkMode}
            />
            <label
              htmlFor="theme-checkbox"
              className="label"
              title="Dark Mode On/Off"
            >
              {/* Sun and Moon SVGs */}
              <span className="ball"></span>
            </label>
          </div>
          <button>Sign UP</button>
          <button>Login</button>
        </div>
      </nav>
    </div>
  );
};

export default Header;
